document.addEventListener("DOMContentLoaded", (() => {
    document.querySelectorAll('input[type="range"][data-sync]').forEach((e => {
        const n = e.dataset.sync;
        document.querySelector(`input[type="number"][data-sync="${n}"]`) || (console.warn(`Sem par <number> para data-sync="${n}"`, e), e.classList.add("is-invalid"))
    }))
}));